<?phpfunction JobPageURL() { $pageURL = 'http'; if ($_SERVER["HTTPS"] == "on") {$pageURL .= "s";} $pageURL .= "://"; if ($_SERVER["SERVER_PORT"] != "80") {  $pageURL .= $_SERVER["SERVER_NAME"].":".$_SERVER["SERVER_PORT"].$_SERVER["REQUEST_URI"]; } else {  $pageURL .= $_SERVER["SERVER_NAME"].$_SERVER["REQUEST_URI"]; } $pageURL =  preg_replace('/&n_startdate([\S]+)/', '', $pageURL); $pageURL =  preg_replace('/&p_startdate([\S]+)/', '', $pageURL); $pageURL =  preg_replace('/n_startdate([\S]+)/', '', $pageURL); $pageURL =  preg_replace('/p_startdate([\S]+)/', '', $pageURL); return $pageURL;}function getYear($date){    return substr($date, 0,4);}function getMonth($date){ return substr($date, 5,2);}function getDay($date){ return substr($date, 8,2);}function getMondayDateOfWeek($date){	   // $date=getdate($date);     $weekday = $date['weekday'];     if($weekday=="Monday"){   return $date ;                 }     if($weekday=="Tuesday"){ return getdate(strtotime("last Monday", $date));}     if($weekday=="Wednesday"){ return getdate(strtotime("last Monday", $date));}     if($weekday=="Thursday"){ return getdate(strtotime("last Monday", $date));}     if($weekday=="Friday"){ return getdate(strtotime("last Monday", $date));}     if($weekday=="Saturday"){ return getdate(strtotime("last Monday", $date));}     if($weekday=="Sunday"){ return getdate(strtotime("last Monday", $date));}}function getMySqlDate($str_date){    $str_date=explode("/",$str_date);    return $str_date[2].'-'.$str_date[0].'-'.$str_date[1];}function getTime($val){    //echo $val;    $time=array(        "0.00"=>"12.00 AM",        "0.50"=>"12.30 AM",        "1.00"=>"01.00 AM",        "1.50"=>"01.30 AM",        "2.00"=>"02.00 AM",        "2.50"=>"02.30 AM",        "3.00"=>"03.00 AM",        "3.50"=>"03.30 AM",        "4.00"=>"04.00 AM",        "4.50"=>"04.30 AM",        "5.00"=>"05.00 AM",        "5.50"=>"05.30 AM",        "6.00"=>"06.00 AM", 